INSERT INTO `preguntas` (`id`, `pregunta`, `resp_a`, `resp_b`, `resp_c`, `resp_d`, `correcta`, `examen_id`, `created_at`, `updated_at`) VALUES
(1, 'La Administración Electrónica...', 'Implica el uso de las TIC en las Administraciones Públicas.', 'Requiere cambios organizativos.', 'Nuevas capacidades en los empleados públicos.', 'Todas las anteriores.', 'resp_d', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(2, 'Una infraestructura de clave pública es el conjunto de elementos hardware y software,...', 'Así como políticas y procedimientos que permiten llevar a cabo la gestión de las aplicaciones de administración electrónica.', 'Que permiten gestionar el ciclo de vida de los certificados digitales.', 'Así como políticas y procedimientos que permiten llevar a cabo la gestión y el control de vida de los certificados digitales.', 'Ninguna de las anteriores es correcta.', 'resp_b', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(3, '¿Quién mantiene actualizada la CRL?', 'La Autoridad de Certificación.', 'La Autoridad de Registro.', 'La Autoridad de Revocación.', 'Los poseedores de un certificado digital.', 'resp_a', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(4, '¿Qué tipo de certificado permite identificar una cualidad, estado o situación?', 'Certificado de representante.', 'Certificado de pertenencia a empresa.', 'Certificado de atributo.', 'Certificado mixto.', 'resp_c', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(5, 'SL proporciona...', 'Autenticación y confidencialidad.', 'Autenticación y no repudio.', 'Autenticación, confidencialidad, integridad y no repudio.', 'Autenticación, confidencialidad e integridad.', 'resp_c', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(6, 'Con respecto a los subprotocolos de TLS/SSL:', 'Record encapsula el tráfico.', 'Handshake se encarga únicamente de la autenticación.', 'Change Cipher Spec cambia a modo cifrado.', 'Todas las respuestas anteriores son correctas.', 'resp_c', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(7, 'Señale la afirmación FALSA con respecto a TLS/SSL...', 'El cliente siempre envía su certificado.', 'El servidor siempre envía su certificado.', 'Conexiones posteriores del mismo cliente reusarán la sesión existente y se establecerán más rápidamente.', 'La sesión se reinicia cuando se reinicia el servidor o el cliente.', 'resp_a', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(8, '¿Cuál es la Ley de Firma Electrónica?', 'Ley59/2003.', 'Ley11/2007.', 'Ley59/1999.', 'Ley15/1999.', 'resp_a', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(9, 'Un certificado digital se corresponde, siendo precisos, con:', 'Firma electrónica.', 'Firma electrónica avanzada.', 'Firma electrónica reconocida.', 'Firma electrónica regulada.', 'resp_c', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(10, '"Aquella dirección electrónica disponible para los ciudadanos a través de redes de telecomunicaciones cuya titularidad, gestión y administración corresponde a una Administració Pública, órgano o entidad administrativa en el ejercicio de sus competencias".', 'Tablón de anuncios digital.', 'Certificado digital del organismo.', 'Sedeelectrónica.', 'Ninguna de las anteriores.', 'resp_c', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(11, '¿Qué ley determina el contexto de agentes y servicios que conforman el nuevo modelo de Administración al que se pretende llegar, y que como podemos deducir, y en materia de identidad digital, se asienta sobre la LFE?', 'Ley11/2003.', 'Ley15/1999.', 'Ley7/2011.', 'Ley11/2007.', 'resp_d', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(12, 'Una "actuación administrativa producida por un sistema de información adecuadamente programado sin necesidad de intervención de una persona física en cada caso singular" se denomina, según la LAECSP:', 'Actuación administrativa automatizada.', 'Actuación administrativa directa.', 'Actuación administrativa mejorada.', 'Actuación administrativa anónima.', 'resp_a', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(13, '¿Qué instrumento se puede emplear para asegurar la validez de un documento impreso?', 'Sello de órgano.', 'Código seguro de verificación.', 'A y B son correctas.', 'Ninguna de las respuestas es correcta.', 'resp_b', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(14, 'Las administraciones públicas admiten sistemas de firma electrónica conforme a lo dispuesto por la LFE. ¿Cuáles de los siguientes son válidos?', 'DNIe, para personas físicas.', 'Certificados digitales.', 'Otros sistemas como, por ejemplo, claves concertadas.', 'Todas las anteriores son correctas.', 'resp_d', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(15, 'El DNI electrónico viene regulado por.', 'Ley59/2003.', 'Real Decreto 1553/2005.', 'Real Decreto 1586/2009.', 'Todas las anteriores son correctas.', 'resp_d', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(16, 'El uso por parte de la Administración de estándares abiertos así como, en su caso y de forma complementaria, de estándares que sean de uso generalizado por los ciudadanos, ¿es uno de los principios de la administración electrónica?', 'Sí, es el principio de neutralidad tecnológica.', 'Sí, es el principio de simplificación administrativa.', 'Sí, es el principio de accesibilidad.', 'No, la ley 11/2007 no recoge expresamente este principio.', 'resp_a', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(17, 'La publicación de actos y comunicaciones en tablón de anuncios electrónico,...', 'Puede complementar al tablón de anuncios presencial, pero no sustituirlo.', 'Puede sustituir o complementar al tablón de anuncios presencial.', 'No se recomienda.', 'No es un concepto recogido por la Ley 11/2007.', 'resp_b', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(18, '¿Qué formato de firma es el más apropiado para la firma longeva de los siguientes?', 'XAdES-A.', 'XAdES-T.', 'XAdES-X.', 'XAdES-C.', 'resp_a', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(19, '¿Qué formato de firma es el más apropiado para su entrega a usuarios?', 'XAdES.', 'CAdES.', 'PAdES.', 'No existen ventajas en optar por un formato u otro.', 'resp_c', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(20, '¿Cuál de los siguientes formatos utilizaría para firma longeva?', 'XAdES-C.', 'PAdES-LTV.', 'EPES Profile.', 'Ninguno de los anteriores.', 'resp_b', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(21, 'Con respecto al formato CAdES-T.', 'Es utilizado por PAdES-BES Profile.', 'Incluye información de sellado de tiempo.', 'A y B son correctas.', 'A y B son falsas.', 'resp_c', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(22, '¿Cuáles de las siguientes enumeraciones incluye un elemento extraño?', 'Firma en lote; Firma Desatendida; Multifirma.', 'Firma Centralizada; Firma digitalizada; Multifirma de lotes.', 'Firma en lote; Multifirma; Multifirma de lotes.', 'Firma en lote; Firma detached; Firma Desatendida.', 'resp_d', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(23, '¿Cuál de las siguientes NO es una opción para la generación del recibo de firma de un documento firmado?', 'QR Code.', 'Código de barras UCC128.', 'Generación de un cajetín de firma.', 'Permalink al perfil del usuario en la aplicación.', 'resp_d', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(24, '¿Es posible la supresión de la necesidad de aportar cualquier documento acreditativo en los procedimientos administrativos (SCSP)?', 'Sí, si es de manera automatizada.', 'Sí, basta con que el ciudadano lo autorice.', 'No, queda restringido a identidad y datos de residencia.', 'Ninguna de las respuestas anteriores es correcta.', 'resp_d', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(25, '¿Cuál de las siguientes es una plataforma para el uso de certificados digitales de uso generalizado en la Administración Pública española?', '@firma.', 'Afirm@.', 'E-firma.', 'Ninguna de las anteriores es correcta.', 'resp_a', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(26, 'La plataforma indicada en la pregunta anterior tiene un software que se ejecuta en cliente llamado Cliente de Firma. ¿Cuál es la afirmación FALSA?', 'Existe una versión ligera del cliente llamada miniapplet del cliente de firma.', 'El miniapplet del cliente de firma está desarrollado íntegramente en javascript para hacerlo más ligero.', 'El Cliente de firma es un applet Java.', 'El miniapplet de firma permite acceder a la clave privada asociada al certificado del cliente.', 'resp_b', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(27, 'La notificación electrónica se puede practicar mediante...', 'Un correo electrónico ordinario.', 'Una aplicación web que registre el acceso a una página determinada.', 'Una aplicación web que registre el acceso a una página determinada, siempre que la identificación sea mediante certificado digital.', 'Ninguna de las anteriores.', 'resp_b', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(28, '¿En qué contexto puede encontrar el Número de Referencia Completo (NRC)?', 'Notificación electrónica.', 'Registro electrónico.', 'Firmaelectrónica.', 'Pago electrónico.', 'resp_d', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(29, '¿Cómo se conoce a la firma múltiple en la que cada firma refrenda a la firma anterior?', 'Cofirma.', 'Multifirma.', 'Contrafirma.', 'Ninguna de las anteriores.', 'resp_c', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(30, 'La definición "Información de cualquier naturaleza en forma electrónica, archivada en un soporte electrónico según un formato determinado y susceptible de identificación y tratamiento diferenciado" se corresponde con:', 'Documento electrónico.', 'Compulsa electrónica.', 'Factura electrónica.', 'Expediente electrónico.', 'resp_a', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(31, 'La identificación mediante funcionario público...', 'Es una forma reconocida de identificación de los ciudadanos.', 'Requiere que el ciudadano y el funcionario cuenten con certificado digital.', 'Se puede usar en cualquier circunstancia.', 'Todas las anteriores son correctas.', 'resp_a', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(32, 'Son características del Código Seguro de Verificación:', 'El carácter único del código generado para cada documento.', 'Su vinculación con el documento generado y con el firmante.', 'Se debe garantizar la posibilidad de verificar el documento por el tiempo que se establezca en la resolución que autorice la aplicación de este procedimiento.', 'Todas las anteriores.', 'resp_d', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(33, 'El Registro General de Protección de Datos contiene:', 'La estructura y los datos de los ficheros de datos de carácter personal que han sido declarados.', 'La estructura de los ficheros de datos de carácter personal que han sido declarados.', 'El listado de los ficheros de datos de carácter personal que han sido declarados.', 'La estructura de los ficheros de datos de carácter personal que han sido declarados por las administraciones públicas.', 'resp_b', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(34, 'Todos los datos de carácter personal recogidos deben cumplir...', 'Principio del consentimiento del afectado.', 'Principio de información.', 'Principio de calidad de los datos.', 'Todas las respuestas anteriores son correctas.', 'resp_d', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(35, '¿Qué significan los derechos ARCO?', 'Acceso, rectificación, cancelación y oposición.', 'Acceso, revisión, cancelación y oposición.', 'Anonimización, rectificación, cancelación y oposición.', 'Acceso, rectificación, cancelación y ofuscación.', 'resp_a', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(36, 'La Protección de Datos de Carácter Personal viene regulada en:', 'Ley 15/1999.', 'R.D. 1720/2007.', 'A y B.', 'Ley 15/1999 y 34/2001.', 'resp_c', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(37, 'El Esquema Nacional de Seguridad viene regulado en: ', 'R.D. 3/2010.', 'R.D. 3/2011.', 'R.D. 4/2010.', 'R.D. 4/2011.', 'resp_a', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
(38, 'El Esquema Nacional de Seguridad recoge:', 'Medidas relacionadas con la organización global de la seguridad.', 'Medidas para proteger la operación del sistema como conjunto integral de componentes para un fin.', 'Protección de activos concretos, según su naturaleza y la calidad exigida por el nivel de seguridad de las dimensiones afectadas. ', 'Todas las respuestas anteriores son correctas.', 'resp_d', 1, '0000-00-00 00:00:00', '0000-00-00 00:00:00'),
